import request from 'supertest';
import express from 'express';
import apiRoutes from '../routes/apiRoutes';
import { AppDataSource } from '../database/dataSource';
import { Read } from '../entity/read.entity';

const image64_1 = "data:image/png;base64,";
const image64_2 = "data:image/png;base64,";

const app = express();
app.use(express.json());
app.use('/api', apiRoutes);
const updateEndpoint = '/api/upload';
const confirmEndpoint = '/api/confirm';


describe('API tests', () => {
    
    it('Should be 200 - ok', async () => {
        const response = await request(app).get('/api/');
        expect(response.status).toBe(200);
    });
    
    it('Should fetch all users', async () => {
        const response = await request(app).get('/api/users');
        expect(response.status).toBe(200);
        expect(response.body).toEqual([{ id: 1, name: 'John Doe' }]);
    });

    it('Should create a new user', async () => {
        const newUser = { id: 2, name: 'Jane Doe' };
        const response = await request(app).post('/api/users').send(newUser);
        expect(response.status).toBe(201);
        expect(response.body).toEqual(newUser);
    });

    // upload route tests
    it('Should fail upload an image', async () => {
        const response = await request(app).post(updateEndpoint).send({});
        expect(response.status).toBe(400);
    });

    it('Should upload an image', async () => {

        if (!AppDataSource.isInitialized) {
            await AppDataSource.initialize();
        }    
        const nowDate = new Date().toISOString();
        const data = {
            "image": image64_1,
            "customer_code": "00000000000000000000000",
            "measure_datetime": nowDate, 
            "measure_type": "gas"
        };
        const response = await request(app).post(updateEndpoint).send(data);
        expect(response.status).toBe(200);
        expect(response.body["image_url"]).toContain("http");
        expect(typeof response.body["measure_value"]).toBe('number');

        // delete after saved
        const readsRepository = AppDataSource.getRepository(Read);
        readsRepository.delete(response.body["measure_uuid"]);
    });


    it('Should confirm a read', async () => {

        if(!AppDataSource.isInitialized){
            await AppDataSource.initialize();  
        }

        // creating data
        const nowDate = new Date().toISOString();
        const data0 = {
            "image": image64_1,
            "customer_code": "00000000000000000000000",
            "measure_datetime": nowDate,
            "measure_type": "water"
        };
        const responseCreation = await request(app).post(updateEndpoint).send(data0);        
        expect(responseCreation.status).toBe(200);

        
        // success
        const dataSuccess = {
            "measure_uuid": responseCreation.body["measure_uuid"],
            "confirmed_value": 14
        };
        const responseSuccess = await request(app).patch(confirmEndpoint).send(dataSuccess);
        expect(responseSuccess.status).toBe(200);
        
        // fail 400
        const responseFail400 = await request(app).patch(confirmEndpoint).send({});
        expect(responseFail400.status).toBe(400);
        
        
        // fail 409
        const dataFail409= {
            "measure_uuid": responseCreation.body["measure_uuid"],
            "confirmed_value": 14
        };
        const responseFail409 = await request(app).patch(confirmEndpoint).send(dataFail409);
        expect(responseFail409.status).toBe(409);

        // fail 404
        const dataFail404 = {
            "measure_uuid": "fae50fad-9b63-48bf-a344-092bdbd5cafd",
            "confirmed_value": 14
        };
        const responseFail404 = await request(app).patch(confirmEndpoint).send(dataFail404);
        expect(responseFail404.status).toBe(404);

        // delete data
        const readsRepository = AppDataSource.getRepository(Read);
        readsRepository.delete(responseCreation.body["measure_uuid"]);

    });

    it('Should get costumer\'s readings', async () => {        
        if(!AppDataSource.isInitialized){
            await AppDataSource.initialize();  
        }
        const custumer_code: string = "00000000000000000000000";
        const uuidList: string[] = [];
        
        // save data in the database
        const readsRepository = AppDataSource.getRepository(Read);
        const readGas: Read = new Read();

        // saving fist data 
        readGas.customer_code = custumer_code;
        readGas.measure_datetime = new Date().toISOString();
        readGas.measure_type = "gas";
        readGas.measure_value = 78913;
        readGas.image_url = "http://www.example.com";
        readGas.has_confirmed = false;
        // add to list        
        uuidList.push((await readsRepository.save(readGas)).measure_uuid);
        
        // clone of readGas
        

        //saving second data
        const readWater: Read = new Read();
        readWater.customer_code = custumer_code;
        readWater.measure_datetime = new Date().toISOString();
        readWater.measure_type = "water";
        readWater.measure_value = 879864;
        readWater.image_url = "http://www.example.com"; 
        readWater.has_confirmed = false;
        
        // add to list        
        uuidList.push((await readsRepository.save(readWater)).measure_uuid);
        
        // simple listing
        const response = await request(app).get(`/api/${custumer_code}/list`);
        expect(response.statusCode).toBe(200);
        expect(response.body["customer_code"]).toBe(custumer_code);
        
        // listing with query parameter
        const responseWithTypeWater = await request(app).get(`/api/${custumer_code}/list?measure_type=WATER`);
        expect(responseWithTypeWater.statusCode).toBe(200);
        
        const responseWithTypeGas = await request(app).get(`/api/${custumer_code}/list?measure_type=GAS`);
        expect(responseWithTypeGas.statusCode).toBe(200);

        // listing with wrong query parameter
        const responseWithTypeLight = await request(app).get(`/api/${custumer_code}/list?measure_type=LIGHT`);
        expect(responseWithTypeLight.statusCode).toBe(400);

        // not found
        const responseNotFound = await request(app).get(`/api/radomcustomercode/list`);
        expect(responseNotFound.statusCode).toBe(404);

        // delete de datas
        uuidList.forEach(uuid => {
            readsRepository.delete(uuid);
        });
    });


});